$(function(){var button=$('#loginButton');var button2=$('#LoginName1');var box=$('#loginBox');var form=$('#loginForm');button.removeAttr('href');button2.mouseup(function(){box.toggle();button2.toggleClass('active')});button.mouseup(function(){box.toggle();button.toggleClass('active')});form.mouseup(function(){return false});$(this).mouseup(function(login){if(!($(login.target).parent('#loginButton').length>0)){button.removeClass('active');box.hide()}})});